mapper.update:
update tb_item_vendor
set vendor_name = #{vendorName,jdbcType=VARCHAR},
  vendor_pn = #{vendorPn,jdbcType=VARCHAR},
  vendor_rank = #{vendorRank,jdbcType=INTEGER}
where vendor_id = #{vendorId,jdbcType=INTEGER}

mapper.select:
select vendor_id, vendor_name, vendor_pn, vendor_rank, create_time
from tb_item_vendor
where vendor_id = #{vendorId,jdbcType=INTEGER}
